value.content:
.activity {
  font-size: 0.8em;
  white-space: nowrap;
  overflow-x: hidden;
  span {
    vertical-align: top;
  }
  .glyphicon {
    margin-top: 4px;
    font-size: 0.6em;
  }
  div {
    display: inline-block;
  }
}

.open-view.TYPE_PLUS_RIGHT-user-Xfollow > .card-frame > .card-header > .card-header-title {
  width: 100%;
  a {
    float: right;
    color: #03998d;
    font-size: 0.8em;
  }
}

.open-view.TYPE_PLUS_RIGHT-user-my_contribution > .card-frame > .card-header {
  padding: 0;
  > .card-header-title {
    > .card-title {
      float: left;
    }
    width: 100%;
    text-align: center;
    .counts {
      border-left: 1px solid black;
      display: inline-block;
      > .item {
        text-align: center;
        padding: 0px 10px 0px 10px;
        background: white;
        border-right: 1px solid black;
        display: inline-block;
      }
    }
  }
}
